CommandUIHandler.CommandAction:
javascript:
            function batchCopyCallback(dialogResult, returnValue)
            {
              if(dialogResult === SP.UI.DialogResult.OK)
              {
                SP.UI.Notify.addNotification('Files copied!');
              }
              else if(dialogResult === SP.UI.DialogResult.cancel)
              {
                SP.UI.Notify.addNotification('User action cancelled!');
              }
              SP.UI.ModalDialog.RefreshPage(SP.UI.DialogResult.OK);
            }

            var ctx = SP.ClientContext.get_current();
            var items = SP.ListOperation.Selection.getSelectedItems(ctx);
            var myItems = '';
            var d;

            for (d in items)
            {
              myItems += '|' + items[d].id;
            }

            var options = {
              url: '{SiteUrl}/_layouts/eCaseWeb/BatchCopyMove.aspx?items=' + myItems + '&source=' + SP.ListOperation.Selection.getSelectedList() + '&sourceurl=\'{SiteUrl}\'',
              width: 900,
              height: 700,
              title: 'eCase - Document Batch Copy/Move',
              showClose: true,
              allowMaximize: false,
              dialogReturnValueCallback: batchCopyCallback };

            SP.UI.ModalDialog.showModalDialog(options);
